   125→// --- HELPER FUNCTIONS ---
   126→export function extractRecordId(url: string | null | undefined): string | null {
   127→  if (!url) return null;
   128→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
   129→  const match = url.match(/([a-f0-9]{24})$/i);
   130→  return match ? match[1] : null;
   131→}
   132→
   133→export function createRecordUrl(appId: string, recordId: string): string {
   134→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
   135→}
   136→
   137→async function callApi(method: string, endpoint: string, data?: any) {
   138→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
   139→    method,
   140→    headers: { 'Content-Type': 'application/json' },
   141→    credentials: 'include',  // Nutze Session Cookies für Auth
   142→    body: data ? JSON.stringify(data) : undefined
   143→  });
   144→  if (!response.ok) throw new Error(await response.text());
   145→  // DELETE returns often empty body or simple status
   146→  if (method === 'DELETE') return true;
   147→  return response.json();
   148→}
   149→
   150→/** Upload a file to LivingApps. Returns the file URL for use in record fields. */
   151→export async function uploadFile(file: File | Blob, filename?: string): Promise<string> {
   152→  const formData = new FormData();
   153→  formData.append('file', file, filename ?? (file instanceof File ? file.name : 'upload'));
   154→  const res = await fetch(`${API_BASE_URL}/files`, {
   155→    method: 'POST',
   156→    credentials: 'include',
   157→    body: formData,
   158→  });
   159→  if (!res.ok) throw new Error(`File upload failed: ${res.status}`);
   160→  const data = await res.json();
   161→  return data.url;
   162→}
