   381→## src/hooks/useDashboardData.ts
   382→```
   383→import { useState, useEffect, useMemo, useCallback } from 'react';
   384→import type { Kategorien, Verkaeufer, Produkte, Bestellungen } from '@/types/app';
   385→import { LivingAppsService } from '@/services/livingAppsService';
   386→
   387→export function useDashboardData() {
   388→  const [kategorien, setKategorien] = useState<Kategorien[]>([]);
   389→  const [verkaeufer, setVerkaeufer] = useState<Verkaeufer[]>([]);
   390→  const [produkte, setProdukte] = useState<Produkte[]>([]);
   391→  const [bestellungen, setBestellungen] = useState<Bestellungen[]>([]);
   392→  const [loading, setLoading] = useState(true);
   393→  const [error, setError] = useState<Error | null>(null);
   394→
   395→  const fetchAll = useCallback(async () => {
   396→    setError(null);
   397→    try {
   398→      const [kategorienData, verkaeuferData, produkteData, bestellungenData] = await Promise.all([
   399→        LivingAppsService.getKategorien(),
   400→        LivingAppsService.getVerkaeufer(),
   401→        LivingAppsService.getProdukte(),
   402→        LivingAppsService.getBestellungen(),
   403→      ]);
   404→      setKategorien(kategorienData);
   405→      setVerkaeufer(verkaeuferData);
   406→      setProdukte(produkteData);
   407→      setBestellungen(bestellungenData);
   408→    } catch (err) {
   409→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   410→    } finally {
   411→      setLoading(false);
   412→    }
   413→  }, []);
   414→
   415→  useEffect(() => { fetchAll(); }, [fetchAll]);
   416→
   417→  const kategorienMap = useMemo(() => {
   418→    const m = new Map<string, Kategorien>();
   419→    kategorien.forEach(r => m.set(r.record_id, r));
   420→    return m;
   421→  }, [kategorien]);
   422→
   423→  const verkaeuferMap = useMemo(() => {
   424→    const m = new Map<string, Verkaeufer>();
   425→    verkaeufer.forEach(r => m.set(r.record_id, r));
   426→    return m;
   427→  }, [verkaeufer]);
   428→
   429→  const produkteMap = useMemo(() => {
   430→    const m = new Map<string, Produkte>();
   431→    produkte.forEach(r => m.set(r.record_id, r));
   432→    return m;
   433→  }, [produkte]);
   434→
   435→  return { kategorien, setKategorien, verkaeufer, setVerkaeufer, produkte, setProdukte, bestellungen, setBestellungen, loading, error, fetchAll, kategorienMap, verkaeuferMap, produkteMap };
   436→}
   437→```
