   454→## src/lib/enrich.ts
   455→```
   456→import type { EnrichedBestellungen, EnrichedProdukte } from '@/types/enriched';
   457→import type { Bestellungen, Kategorien, Produkte, Verkaeufer } from '@/types/app';
   458→import { extractRecordId } from '@/services/livingAppsService';
   459→
   460→// eslint-disable-next-line @typescript-eslint/no-explicit-any
   461→function resolveDisplay(url: string | undefined, map: Map<string, any>, ...fields: string[]): string {
   462→  if (!url) return '';
   463→  const id = extractRecordId(url);
   464→  if (!id) return '';
   465→  const r = map.get(id);
   466→  if (!r) return '';
   467→  return fields.map(f => String(r.fields[f] ?? '')).join(' ').trim();
   468→}
   469→
   470→interface ProdukteMaps {
   471→  kategorienMap: Map<string, Kategorien>;
   472→  verkaeuferMap: Map<string, Verkaeufer>;
   473→}
   474→
   475→export function enrichProdukte(
   476→  produkte: Produkte[],
   477→  maps: ProdukteMaps
   478→): EnrichedProdukte[] {
   479→  return produkte.map(r => ({
   480→    ...r,
   481→    kategorieName: resolveDisplay(r.fields.kategorie, maps.kategorienMap, 'kategorie_name'),
   482→    verkaeuferName: resolveDisplay(r.fields.verkaeufer, maps.verkaeuferMap, 'firma_name'),
   483→  }));
   484→}
   485→
   486→interface BestellungenMaps {
   487→  produkteMap: Map<string, Produkte>;
   488→}
   489→
   490→export function enrichBestellungen(
   491→  bestellungen: Bestellungen[],
   492→  maps: BestellungenMaps
   493→): EnrichedBestellungen[] {
   494→  return bestellungen.map(r => ({
   495→    ...r,
   496→    produkteName: resolveDisplay(r.fields.produkte, maps.produkteMap, 'produkt_name'),
   497→  }));
   498→}
   499→
