   507→export function formatDate(s: string | undefined) {
   508→  if (!s) return '—';
   509→  try { return format(parseISO(s), 'dd.MM.yyyy', { locale: de }); } catch { return s; }
   510→}
   511→
   512→export function formatCurrency(v: number | undefined) {
   513→  if (v == null) return '—';
   514→  return new Intl.NumberFormat('de-DE', { style: 'currency', currency: 'EUR' }).format(v);
   515→}
