   563→
   564→export async function chatCompletion(
   565→  messages: ChatMessage[],
   566→  options: CompletionOptions = {}
   567→): Promise<string> {
   568→  const res = await fetch(AI_ENDPOINT, {
   569→    method: "POST",
   570→    headers: { "Content-Type": "application/json", Accept: "application/json" },
   571→    body: JSON.stringify({ model: AI_MODEL, messages, ...options }),
   572→  });
   573→  if (!res.ok) {
   574→    const body = await res.text();
   575→    throw new Error(`AI API ${res.status}: ${body}`);
   576→  }
   577→  const data = await res.json();
   578→  return data.choices[0].message.content;
   579→}
   580→
   581→export async function safeJsonCompletion<T = unknown>(
   582→  messages: ChatMessage[],
   583→  options: CompletionOptions = {}
   584→): Promise<T> {
   585→  const raw = await chatCompletion(messages, options);
   586→  const match = raw.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
   587→  if (!match) throw new Error(`Expected JSON but got: ${raw.slice(0, 200)}`);
   588→  return JSON.parse(match[0]);
   589→}
   590→
   591→export async function withRetry<T>(fn: () => Promise<T>, maxAttempts = 3): Promise<T> {
   592→  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   593→    try {
   594→      return await fn();
   595→    } catch (err) {
   596→      if (attempt === maxAttempts) throw err;
   597→      await new Promise((r) => setTimeout(r, 1000 * attempt));
   598→    }
   599→  }
   600→  throw new Error("unreachable");
   601→}
