   603→// --- File encoding helpers ---
   604→
   605→export function fileToDataUri(file: File): Promise<string> {
   606→  return new Promise((resolve, reject) => {
   607→    const reader = new FileReader();
   608→    reader.onload = () => resolve(reader.result as string);
   609→    reader.onerror = () => reject(new Error("Failed to read file"));
   610→    reader.readAsDataURL(file);
   611→  });
   612→}
   613→
   614→export async function urlToDataUri(url: string): Promise<string> {
   615→  const res = await fetch(url);
   616→  const blob = await res.blob();
   617→  return new Promise((resolve, reject) => {
   618→    const reader = new FileReader();
   619→    reader.onload = () => resolve(reader.result as string);
   620→    reader.onerror = () => reject(new Error("Failed to encode file"));
   621→    reader.readAsDataURL(blob);
   622→  });
   623→}
   624→
   625→/** Convert a data URI back to a Blob (for file uploads) */
   626→export function dataUriToBlob(dataUri: string): Blob {
   627→  const [header, base64] = dataUri.split(',');
   628→  const mime = header.match(/:(.*?);/)?.[1] ?? 'application/octet-stream';
   629→  const bytes = atob(base64);
   630→  const arr = new Uint8Array(bytes.length);
   631→  for (let i = 0; i < bytes.length; i++) arr[i] = bytes.charCodeAt(i);
   632→  return new Blob([arr], { type: mime });
